class TeacherCabinet:
    def __init__(self, teacher_name):
        self.teacher_info = {"–ü–Ü–ë": teacher_name, "–°—Ç–∞—Ç—É—Å": "–ê–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω–æ"}
        self.schedule = []
        self.journals = {}
        self.exam_reports = []

    def add_lesson_from_list(self, time, subject_index, group_name):
        """–î–æ–¥–∞—î –∑–∞–Ω—è—Ç—Ç—è, –≤–∏–∫–æ—Ä–∏—Å—Ç–æ–≤—É—é—á–∏ —ñ–Ω–¥–µ–∫—Å–∏ –∑ SUBJECTS_LIST"""
        if subject_index < len(SUBJECTS_LIST) and group_name in GROUPS_DATA:
            subject = SUBJECTS_LIST[subject_index]
            lesson = {
                "–ß–∞—Å": time,
                "–î–∏—Å—Ü–∏–ø–ª—ñ–Ω–∞": subject,
                "–ì—Ä—É–ø–∞": group_name,
                "–°—Ç—É–¥–µ–Ω—Ç–∏": GROUPS_DATA[group_name]
            }
            self.schedule.append(lesson)
            print(f"‚úÖ –î–æ–¥–∞–Ω–æ: {subject} –¥–ª—è –≥—Ä—É–ø–∏ {group_name}")
        else:
            print("‚ùå –ü–æ–º–∏–ª–∫–∞: –ù–µ–≤—ñ—Ä–Ω–∏–π –ø—Ä–µ–¥–º–µ—Ç –∞–±–æ –Ω–∞–∑–≤–∞ –≥—Ä—É–ø–∏.")

    def create_journal_for_group(self, group_name, subject_index):
        """–ê–≤—Ç–æ–º–∞—Ç–∏—á–Ω–æ —Å—Ç–≤–æ—Ä—é—î –∂—É—Ä–Ω–∞–ª –Ω–∞ –æ—Å–Ω–æ–≤—ñ —Ä–µ–∞–ª—å–Ω–∏—Ö –¥–∞–Ω–∏—Ö –≥—Ä—É–ø"""
        subject = SUBJECTS_LIST[subject_index]
        if group_name in GROUPS_DATA:
            journal_id = f"{subject}_{group_name}"
            self.journals[journal_id] = {
                "–¥–∏—Å—Ü–∏–ø–ª—ñ–Ω–∞": subject,
                "–≥—Ä—É–ø–∞": group_name,
                "—Å—Ç—É–¥–µ–Ω—Ç–∏": {name: {"–æ—Ü—ñ–Ω–∫–∏": [], "–ø—Ä–∏—Å—É—Ç–Ω—ñ—Å—Ç—å": True} for name in GROUPS_DATA[group_name]}
            }
            print(f"üìñ –°—Ç–≤–æ—Ä–µ–Ω–æ –∂—É—Ä–Ω–∞–ª: {subject} ({group_name}). –ö—ñ–ª—å–∫—ñ—Å—Ç—å —Å—Ç—É–¥–µ–Ω—Ç—ñ–≤: {len(GROUPS_DATA[group_name])}")
        else:
            print(f"‚ùå –ì—Ä—É–ø—É {group_name} –Ω–µ –∑–Ω–∞–π–¥–µ–Ω–æ.")

    def set_auto_grade(self, journal_id, student_name, ects_grade):
        """–ü—Ä–æ—Å—Ç–∞–≤–ª—è—î –æ—Ü—ñ–Ω–∫—É –∑ –∞–≤—Ç–æ–º–∞—Ç–∏—á–Ω–æ—é –∫–æ–Ω–≤–µ—Ä—Ç–∞—Ü—ñ—î—é –≤ –Ω–∞—Ü—ñ–æ–Ω–∞–ª—å–Ω—É —à–∫–∞–ª—É"""
        mapping = {"A": "–í—ñ–¥–º—ñ–Ω–Ω–æ", "B": "–î–æ–±—Ä–µ", "C": "–î–æ–±—Ä–µ", "D": "–ó–∞–¥–æ–≤—ñ–ª—å–Ω–æ", "E": "–ó–∞–¥–æ–≤—ñ–ª—å–Ω–æ", "F": "–ù–µ–∑–∞–¥–æ–≤—ñ–ª—å–Ω–æ"}
        if journal_id in self.journals and student_name in self.journals[journal_id]["—Å—Ç—É–¥–µ–Ω—Ç–∏"]:
            national = mapping.get(ects_grade, "–Ω/–∞")
            entry = {"ECTS": ects_grade, "–ù–∞—Ü": national, "–î–∞—Ç–∞": datetime.now().strftime("%d.%m.%Y")}
            self.journals[journal_id]["—Å—Ç—É–¥–µ–Ω—Ç–∏"][student_name]["–æ—Ü—ñ–Ω–∫–∏"].append(entry)
            print(f"üìù {student_name}: {ects_grade} ({national})")

